mapper.select:
select k.*, (select t.fc_code from SYS_FRM_CODE t where t.fc_pk=k.bcgt) as bcgtcode, (select t.fc_name from SYS_FRM_CODE t where t.fc_pk=k.qwlx_pk) as qw_name, (select b.dept_name from SYS_DEPART_INFO b where b.dept_pk=k.dept_pk) as dept_name from QWGL_BC_INFO k
and k.bc_name like '%'||#{bc_name}||'%'
and k.dept_pk = #{dept_pk}
and k.qwlx_pk = #{qwlx_pk}
and k.sfqy = #{sfqy}

select t.dept_pk,t.dept_code,t.dept_name as name,t.DEPT_SUPERIOR,t.geo_spatial from sys_depart_info t
t.DEPT_SUPERIOR is null
t.DEPT_SUPERIOR=#{_parameter}

mapper.update:
update QWGL_BC_INFO
bc_name = #{bc_name,jdbcType=VARCHAR}, dept_pk = #{dept_pk,jdbcType=VARCHAR}, qwlx_pk = #{qwlx_pk,jdbcType=VARCHAR}, sfqy = #{sfqy,jdbcType=VARCHAR}, bc_kssj = to_date(#{bc_kssj},'yyyy-mm-dd hh24:mi'), bc_jssj = to_date(#{bc_jssj},'yyyy-mm-dd hh24:mi'), bcgt = #{bcgt,jdbcType=VARCHAR}, span_cycle = #{span_cycle,jdbcType=VARCHAR},
where bc_pk=#{bc_pk}
