mapper.select:
		select * from BSUSER.JCBK_CAR_RECO
		
			
				and hpzl = #{hpzl}
			
			
				and hphm like '%'||#{hphm}||'%'
			

			
				and ssdl = #{ssdl}
			

			
				and sfhbc = #{sfhbc}
			
			
				and sfwxpc = #{sfwxpc}
			
			
				and zjsaqdzt = #{zjsaqdzt}
			
			
				and fjsaqdzt = #{fjsaqdzt}
			
			
				and sfddh = #{sfddh}
			

			
		     and jgsj >=to_date(#{kssj},'yyyy-mm-dd HH24:mi:ss')
			
			
		    and jgsj <= to_date(#{jssj},'yyyy-mm-dd HH24:mi:ss') 
			
			
				and SBBH in
				
					#{item}
				

			
			
				and kkbh in
				
					#{item}
				

			
		
		order by jgsj desc

select t.dept_pk,t.dept_code,t.dept_name as name,t.DEPT_SUPERIOR from sys_depart_info t 

		  
		     t.DEPT_SUPERIOR is null
		   
		     
		     t.DEPT_SUPERIOR=#{_parameter}

		         select t.ROIM_CODE as tbec_kkbh ,t.roim_name as tbei_name from C_ROAD_ITEM t where t.roim_code in (
select t.belonged_road_id from JCSJ_DEVICE t where t.device_num in

		            #{w}
		        
group by t.belonged_road_id
 )

select t.tbek_pk pk,t.tbek_code key,t.tbek_name title,t.tbek_parent
parent_pk from C_KREIS_CODE t  where 1=1
	
	
		and t.tbek_code=#{tbek_code}
	
	
		
		and t.tbek_parent=#{parent_pk}
	
	
		and t.TBEK_PARENT is null

select ROIM_CODE key,roim_name title,roim_type road_type from C_ROAD_ITEM where 1=1
and instr(tbek_code,#{tbek_code})>0
and dept_code=#{dept_code}

select s.DEVICE_NUM key,s.DEVICE_NAME title from JCSJ_DEVICE
s where s.BELONGED_ROAD_ID=#{code}
and fk_device_type='KK'
