 and WFSJ > to_date(#{wfsj},'yyyy-mm-dd HH24:mi:ss')-5/24/60/60 
   and WFSJ < to_date(#{wfsj},'yyyy-mm-dd HH24:mi:ss')+5/24/60/60 
and WFDD=#{wfdd}
and SHBZ in('1')

select * from OEUSER.TBL_OFFE_EVDI
where wfpk=#{wfpk}
and SHBZ in('0','15')

