mapper.select:
 

 
  select t.FK_DEPT_ID ,t.EVALUATE_DATE_TYPE,t.FK_USER_ID,t.pk_id,t.remarks,
t.evaluate_date_end,t.evaluate_date_start
 from QW_I_C_METRICS t 

 
		
			
				and t.fk_dept_id=#{fk_dept_id}
			


			
		
				and  t.fk_user_id=#{fk_user_id}
			
			
			
				and t.fk_user_id like '%'||#{fk_user_id}||'%'
			

	
				and t.evaluate_date_type=#{evaluate_date_type}
			

			
			and t.METRICS_TYPE=1

 
			select t.fk_relation_id ,sum(t.metrics_num) num from qw_evaluate_metrics t 
     where t.fk_relation_id=#{pk_id}
    
    group by t.fk_relation_id

mapper.insert:
SELECT cistsys.SEQ_TXZ_PERMIT.nextval FROM DUAL
insert into QW_I_C_METRICS ( pk_id, fk_user_id, fk_dept_id, metrics_type, evaluate_date_type, evaluate_date_start, evaluate_date_end, remarks
)
values ( #{pk}, #{arrays}, #{fk_dept_id}, #{metrics_type}, #{evaluate_date_type}, to_date(#{evaluate_date_start},'yyyy-mm-dd HH24:mi:ss'), to_date(#{evaluate_date_end},'yyyy-mm-dd HH24:mi:ss'), #{remarks}
)

mapper.delete:
delete FROM QW_I_C_METRICS
WHERE pk_id in

	#{pk}

delete FROM QW_EVALUATE_METRICS
WHERE FK_RELATION_ID in

	#{pks}

mapper.select:
select t.dept_pk,t.dept_code,t.dept_name as name,t.DEPT_SUPERIOR from sys_depart_info t 

		  
		     t.DEPT_SUPERIOR is null
		   
		     
		     t.DEPT_SUPERIOR=#{_parameter}

select t.dept_pk,t.dept_code,t.dept_name as name,t.DEPT_SUPERIOR from sys_depart_info t 

		  
		     t.DEPT_SUPERIOR is null
		   
		     
		     t.DEPT_SUPERIOR=#{_parameter}
